        const errorMessage = `Failed to fetch user ${userId}: Network error.`;
        dispatch({
          type: FETCH_USER_FAILURE,
          payload: { userId, error: errorMessage },
        });
        console.error(errorMessage);
      }
    }, 1000); // Simulate 1 second network delay
  };
};

