import { createStore, applyMiddleware, combineReducers, Dispatch, AnyAction, Store } from 'redux';

// --- Reducer (for store setup) ---
interface AppState { log: string[]; }
const initialAppState: AppState = { log: [] };
function appReducer(state: AppState = initialAppState, action: AnyAction): AppState {
  if (action.type === 'ADD_LOG') {
    return { ...state, log: [...state.log, action.payload] };
  }
  return state;
}
const rootReducer = combineReducers<AppState>({ app: appReducer });

// --- Implement `createMyCustomThunkMiddleware` ---
function createMyCustomThunkMiddleware(extraArgument?: any) {
  return ({ dispatch, getState }: { dispatch: Dispatch; getState: () => any }) => 
  (next: Dispatch) => 
  (action: AnyAction) => {
    // If the dispatched action is a function, call it with dispatch, getState, and extraArgument.
    if (typeof action === 'function') {
      console.log('[MyCustomThunk] Intercepted a function action. Executing...');
      // Execute the thunk and return its result. This is crucial for promise chaining.
      return action(dispatch, getState, extraArgument);
    }

    // Otherwise, it's a plain object action; pass it to the next middleware or reducer.
    console.log('[MyCustomThunk] Intercepted a plain object action. Passing to next...');
    return next(action);
  };
}

// --- Example usage ---
interface MyCustomAPI {
  fetchUser: (id: string) => Promise<{ id: string; name: string; }>;
}

const myCustomAPI: MyCustomAPI = {
  fetchUser: (id: string) => {
    console.log(`Simulating API call for user ${id}...`);
    return new Promise(resolve => {
      setTimeout(() => {
        resolve({ id, name: `User ${id} from API` });
      }, 500);
    });
  }
};

const customThunk = createMyCustomThunkMiddleware(myCustomAPI); // Pass our custom API client as extraArgument
const store = createStore(rootReducer, applyMiddleware(customThunk));

// Define a thunk that uses the injected API
const fetchUserThunk = (userId: string) => (
  dispatch: Dispatch,
  getState: () => AppState,
  api: MyCustomAPI // This 'api' parameter will receive `myCustomAPI`
) => {
  dispatch({ type: 'ADD_LOG', payload: `[Thunk] Starting fetch for user ${userId}...` });
  api.fetchUser(userId)
    .then(user => {
      dispatch({ type: 'ADD_LOG', payload: `[Thunk] User fetched: ${user.name}` });
      // Dispatch a regular action if needed, e.g., to save user to state
      // dispatch({ type: 'USER_FETCHED', payload: user }); 
    })
    .catch(error => {
      dispatch({ type: 'ADD_LOG', payload: `[Thunk] Error fetching user ${userId}: ${error.message}` });
    });
};

console.log('Initial Store Log:', store.getState().app.log);
store.dispatch(fetchUserThunk('789'));
store.dispatch({ type: 'ADD_LOG', payload: 'Plain action dispatched.' });

// Wait for async operations to complete and check final state
setTimeout(() => {
  console.log('Final Store Log:', store.getState().app.log);
}, 1000);

