import { createStore, applyMiddleware, combineReducers, Dispatch, AnyAction } from 'redux';
import thunk from 'redux-thunk';

// 1. Define Action Types
const FETCH_ITEMS_REQUEST = 'FETCH_ITEMS_REQUEST';
const FETCH_ITEMS_SUCCESS = 'FETCH_ITEMS_SUCCESS';
const FETCH_ITEMS_FAILURE = 'FETCH_ITEMS_FAILURE';

// State Interfaces
interface Item {
  userId: number;
  id: number;
  title: string;
  body: string;
}

interface ItemsState {
  items: Item[];
  loading: boolean;
  error: string | null;
}

const initialItemsState: ItemsState = {
  items: [],
  loading: false,
  error: null,
};

// 2. Implement itemsReducer
function itemsReducer(state: ItemsState = initialItemsState, action: AnyAction): ItemsState {
  switch (action.type) {
    case FETCH_ITEMS_REQUEST:
      return { ...state, loading: true, error: null };
    case FETCH_ITEMS_SUCCESS:
      return { ...state, loading: false, error: null, items: action.payload };
    case FETCH_ITEMS_FAILURE:
      return { ...state, loading: false, items: [], error: action.payload };
    default:
      return state;
  }
}

const rootReducer = combineReducers({ items: itemsReducer });

// Store creation
export const store = createStore(rootReducer, applyMiddleware(thunk));

// 3. Implement fetchItems thunk action creator
export const fetchItems = () => {
  return async (dispatch: Dispatch) => {
    dispatch({ type: FETCH_ITEMS_REQUEST });

    try {
      const response = await fetch('https://jsonplaceholder.typicode.com/posts');
      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }
      const data: Item[] = await response.json();
      dispatch({
        type: FETCH_ITEMS_SUCCESS,
        payload: data,
      });
      console.log('Items fetched successfully!');
    } catch (error: any) {
      dispatch({
        type: FETCH_ITEMS_FAILURE,
        payload: error.message,
      });
      console.error('Failed to fetch items:', error.message);
    }
  };
};

