// Use setInterval to log state changes over time, as fetch is truly async
const intervalId = setInterval(() => {
  const currentState = store.getState().items;
  console.log('Current State (polled):', JSON.stringify(currentState, null, 2));
  if (!currentState.loading) {
    clearInterval(intervalId); // Stop polling once loading is false
    console.log('Finished loading. Final items count:', currentState.items.length);
  }
}, 500); // Check state every 0.5 seconds

