import { createSelector } from 'reselect';

// --- Dummy Redux State Structure ---
const dummyState = {
  cart: {
    items: [
      { id: 'p1', name: 'Laptop', price: 1200, quantity: 1 },
      { id: 'p2', name: 'Mouse', price: 25, quantity: 2 },
      { id: 'p3', name: 'Keyboard', price: 75, quantity: 1 }
    ]
  },
  user: { name: 'John Doe' },
  settings: { theme: 'dark' }
};

// --- Your Selectors Here ---

// 1. Basic selector for raw cart items
export const selectCartItems = (state) => {
  // console.log('Running selectCartItems'); // For debugging memoization
  return state.cart.items;
};

// 2. Memoized selector for total quantity
export const selectTotalItemsInCart = createSelector(
  [selectCartItems],
  (items) => {
    console.log('Recalculating total items...'); // Monitor re-computation
    return items.reduce((total, item) => total + item.quantity, 0);
  }
);

// 3. Memoized selector for total price
export const selectTotalPrice = createSelector(
  [selectCartItems],
  (items) => {
    console.log('Recalculating total price...'); // Monitor re-computation
    return items.reduce((total, item) => total + (item.price * item.quantity), 0);
  }
);

// 4. Memoized selector for cart summary
export const selectCartSummary = createSelector(
  [selectTotalItemsInCart, selectTotalPrice],
  (totalItems, totalPrice) => {
    console.log('Recalculating cart summary...'); // Monitor re-computation
    return { totalItems, totalPrice };
  }
);

