import { Stack, fromJS } from 'immutable';

class UndoRedoManager<T> {
  private undoStack: Stack<T>;
  private redoStack: Stack<T>;
  private currentState: T;

  constructor(initialState: T) {
    this.currentState = initialState;
    this.undoStack = Stack<T>();
    this.redoStack = Stack<T>();
  }

  executeAction(newState: T): T {
    // Push current state onto undo stack
    this.undoStack = this.undoStack.push(this.currentState);
    // Update current state
    this.currentState = newState;
    // Clear redo stack on new action
    this.redoStack = Stack<T>();
    return this.currentState;
  }

  undo(): T {
    if (this.undoStack.isEmpty()) {
      console.warn('Cannot undo: undo stack is empty.');
      return this.currentState;
    }
    // Move current state to redo stack
    this.redoStack = this.redoStack.push(this.currentState);
    // Restore previous state from undo stack
    this.currentState = this.undoStack.peek()!;
    this.undoStack = this.undoStack.pop();
    return this.currentState;
  }

  redo(): T {
    if (this.redoStack.isEmpty()) {
      console.warn('Cannot redo: redo stack is empty.');
      return this.currentState;
    }
    // Move current state to undo stack
    this.undoStack = this.undoStack.push(this.currentState);
    // Restore next state from redo stack
    this.currentState = this.redoStack.peek()!;
    this.redoStack = this.redoStack.pop();
    return this.currentState;
  }

  getCurrentState(): T {
    return this.currentState;
  }

  // Optional: For debugging/testing
  getUndoStackSize(): number {
    return this.undoStack.size;
  }
  getRedoStackSize(): number {
    return this.redoStack.size;
  }
}

