const request = indexedDB.open('MyDatabase', 1);

request.onupgradeneeded = function(event) {
  const db = event.target.result;
  // Create an object store named 'customers' with 'id' as the key path
  const objectStore = db.createObjectStore('customers', { keyPath: 'id' });

  // Create indexes for faster searching
  // 'name' index, based on 'name' property, not unique
  objectStore.createIndex('name', 'name', { unique: false });
  // 'email' index, based on 'email' property, must be unique
  objectStore.createIndex('email', 'email', { unique: true });
};

request.onerror = function(event) {
  console.error('Database error:', event.target.errorCode);
};

request.onsuccess = function(event) {
  console.log('Database opened successfully.');
  // Database is ready for transactions here
  const db = event.target.result;
  db.close(); // Close db after setup if no immediate operations
};

const request = indexedDB.open('MyDatabase', 1);

request.onsuccess = function(event) {
  const db = event.target.result;

  // Start a readwrite transaction for the 'customers' object store
  const transaction = db.transaction(['customers'], 'readwrite');
  const objectStore = transaction.objectStore('customers');

  // --- Add Data ---
  const customerData = { id: 1, name: 'John Doe', email: 'john@example.com', age: 30 };
  const addRequest = objectStore.add(customerData);
  addRequest.onsuccess = () => console.log('Customer added:', customerData);
  addRequest.onerror = (e) => console.error('Add error:', e.target.error);

  // --- Read Data ---
  const getRequest = objectStore.get(1);
  getRequest.onsuccess = function(event) {
    const customer = event.target.result;
    console.log('Customer retrieved:', customer);
  };
  getRequest.onerror = (e) => console.error('Get error:', e.target.error);

  // --- Update Data ---
  // Note: 'put' can add if key doesn't exist, or update if it does.
  const updatedCustomerData = { ...customerData, age: 31 };
  const updateRequest = objectStore.put(updatedCustomerData);
  updateRequest.onsuccess = () => console.log('Customer updated:', updatedCustomerData);
  updateRequest.onerror = (e) => console.error('Update error:', e.target.error);

  // --- Delete Data ---
  const deleteRequest = objectStore.delete(1);
  deleteRequest.onsuccess = () => console.log('Customer deleted with ID 1.');
  deleteRequest.onerror = (e) => console.error('Delete error:', e.target.error);

  // --- Use an index to search ---
  const nameIndex = objectStore.index('name');
  const nameSearchRequest = nameIndex.getAll('John Doe'); // Get all records where 'name' is 'John Doe'
  nameSearchRequest.onsuccess = function(event) {
    const matches = event.target.result;
    console.log('Customers with name "John Doe":', matches);
  };
  nameSearchRequest.onerror = (e) => console.error('Index search error:', e.target.error);

  // A transaction completes when all its requests complete and there are no new requests.
  transaction.oncomplete = () => console.log('Transaction completed.');
  transaction.onerror = (e) => console.error('Transaction error:', e.target.error);
};

