function performIndexedDBOperations() {
  const request = indexedDB.open('ProductsDB', 1);

  request.onupgradeneeded = function(event) {
    const db = event.target.result;
    if (!db.objectStoreNames.contains('products')) {
      db.createObjectStore('products', { keyPath: 'id' });
      console.log('Object store "products" created.');
    }
  };

  request.onsuccess = function(event) {
    const db = event.target.result;
    const transaction = db.transaction(['products'], 'readwrite');
    const objectStore = transaction.objectStore('products');

    // Add data
    const productToAdd = { id: 1, name: 'Laptop', price: 1200 };
    const addRequest = objectStore.add(productToAdd);
    addRequest.onsuccess = () => console.log('Product added:', productToAdd);
    addRequest.onerror = (e) => console.error('Error adding product:', e.target.error);

    // Get data
    const getRequest = objectStore.get(1);
    getRequest.onsuccess = function(event) {
      const product = event.target.result;
      console.log('Product retrieved:', product);
    };
    getRequest.onerror = (e) => console.error('Error retrieving product:', e.target.error);

    transaction.oncomplete = () => db.close();
    transaction.onerror = (e) => console.error('Transaction error:', e.target.error);
  };

  request.onerror = function(event) {
    console.error('Database open error:', event.target.errorCode);
  };
}

// Call the function to execute operations
performIndexedDBOperations();
```

