```javascript
// prime_calculator.js
self.onmessage = function(event) {
  const { type, limit } = event.data;

  if (type === 'start') {
    try {
      const primes = sieveOfEratosthenes(limit, (progress) => {
        // Send progress updates back to the main thread
        self.postMessage({ type: 'progress', value: progress });
      });
      const maxPrime = primes.length > 0 ? primes[primes.length - 1] : 0;
      self.postMessage({ type: 'result', count: primes.length, maxPrime: maxPrime });
    } catch (e) {
      self.postMessage({ type: 'error', message: e.message });
    }
  }
};

function sieveOfEratosthenes(limit, onProgress) {
  if (limit < 2) return [];
  const isPrime = new Array(limit + 1).fill(true);
  isPrime[0] = isPrime[1] = false; // 0 and 1 are not prime

  const sqrtLimit = Math.sqrt(limit);
  for (let p = 2; p <= sqrtLimit; p++) {
    if (isPrime[p]) {
      for (let multiple = p * p; multiple <= limit; multiple += p) {
        isPrime[multiple] = false;
      }
    }
  }

  const primes = [];
  const totalSteps = limit;
  let lastProgress = 0;

  for (let i = 2; i <= limit; i++) {
    if (isPrime[i]) {
      primes.push(i);
    }
    // Send progress update periodically
    const currentProgress = Math.floor((i / totalSteps) * 100);
    if (currentProgress > lastProgress && currentProgress % 5 === 0) { // Update every 5%
      onProgress(currentProgress);
      lastProgress = currentProgress;
    }
  }
  onProgress(100); // Ensure 100% is always sent at the end
  return primes;
}
