// Check if Web Workers are supported
if (window.Worker) {
  const myWorker = new Worker('worker.js');

  // Send data to the worker
  myWorker.postMessage('Start calculation');
  console.log('Message sent to worker');

  // Listen for messages from the worker
  myWorker.onmessage = function(event) {
    console.log('Received from worker:', event.data);
    // Update UI based on worker's result (e.g., display result)
    const resultElement = document.getElementById('result');
    if (resultElement) {
      resultElement.textContent = 'Worker says: ' + event.data;
    }
  };

  // Handle errors from the worker
  myWorker.onerror = function(error) {
    console.error('Worker error:', error);
  };

  // Example of terminating the worker after some time
  setTimeout(() => {
    myWorker.terminate();
    console.log('Worker terminated.');
  }, 5000);
} else {
  console.log('Web Workers are not supported in this browser.');
}

// worker.js

self.onmessage = function(event) {
  console.log('Received from main thread in worker:', event.data);
  // Perform a heavy calculation (e.g., Fibonacci)
  const calculateFibonacci = (n) => {
    if (n <= 1) return n;
    return calculateFibonacci(n - 1) + calculateFibonacci(n - 2);
  };

  const result = calculateFibonacci(40); // A moderately heavy calculation

  // Send data back to the main thread
  self.postMessage('Calculation complete! Result: ' + result);
};

// Handle errors from the worker itself
self.onerror = function(error) {
  console.error('Error within worker:', error);
};

// Optional: respond to termination
self.onclose = function() {
  console.log('Worker is about to be terminated.');
};

