function validateDoctype(htmlString) {
  if (!htmlString || typeof htmlString !== 'string') {
    return false;
  }

  // Regex to match the DOCTYPE declaration at the very beginning,
  // ignoring leading whitespace. It captures the standard HTML5 DOCTYPE
  // case-insensitively for 'DOCTYPE' and 'html'.
  const regex = /^\s*<!DOCTYPE\s+html>/i;

  // The original problem statement implies it must be the *very first* non-whitespace.
  // If we consider '<!-- comment -->\n<!DOCTYPE html>' as invalid due to the comment,
  // then simple regex for leading whitespace is enough, as comments are not whitespace.
  // If comments before DOCTYPE were allowed, the regex would be more complex.

  // Trim leading whitespace and then check if it starts with the DOCTYPE.
  // This handles cases like '   <!DOCTYPE html>' correctly.
  const trimmedString = htmlString.trimLeft();

  // Check if the trimmed string starts with the case-insensitive HTML5 DOCTYPE.
  // This correctly accounts for the example '<!-- comment -->\n<!DOCTYPE html>' being false.
  return /^<!DOCTYPE html>/i.test(trimmedString);
}
