enum UserRole {
  ADMIN = "ADMIN",
  EDITOR = "EDITOR",
  VIEWER = "VIEWER",
}

// A tuple to store user information: [id, name, role]
type UserInfoTuple = [number, string, UserRole];

function processUser(user: UserInfoTuple): string {
  const [id, name, role] = user;
  if (role === UserRole.ADMIN) {
    return `Admin User: ${name} (ID: ${id})`;
  } else if (role === UserRole.EDITOR) {
    return `Editor User: ${name} (ID: ${id})`;
  } else {
    return `Viewer User: ${name} (ID: ${id})`;
  }
}

const adminUser: UserInfoTuple = [101, "Alice", UserRole.ADMIN];
const editorUser: UserInfoTuple = [202, "Bob", UserRole.EDITOR];
const viewerUser: UserInfoTuple = [303, "Charlie", UserRole.VIEWER];

console.log(processUser(adminUser));
console.log(processUser(editorUser));
console.log(processUser(viewerUser));

// Example of array of a specific type
const userIds: number[] = [1, 5, 9, 13];
userIds.forEach(id => console.log(`User ID: ${id}`));

function parseInput(input: any): any {
  if (typeof input === 'string') {
    try {
      return JSON.parse(input);
    } catch (e) {
      console.error("Could not parse JSON string");
      return null; // Return null if parsing fails
    }
  } else if (typeof input === 'number') {
    return input * 2;
  } else if (typeof input === 'boolean') {
    return !input;
  }
  return undefined; // Default return for unhandled types
}

let unknownData: any = "{\"name\": \"Alice\", \"age\": 30}";
let processedData = parseInput(unknownData);
console.log(processedData); // Output: { name: 'Alice', age: 30 }

unknownData = 10;
processedData = parseInput(unknownData);
console.log(processedData); // Output: 20

unknownData = true;
processedData = parseInput(unknownData);
console.log(processedData); // Output: false

// Pitfall: 'any' allows invalid calls at compile time
let riskyVariable: any = "hello";
// riskyVariable.map(); // No compile-time error, but will crash at runtime

