import React, { useState } from 'react';

// 1. CounterDisplay Component
function CounterDisplay({ count }) {
  return (
    <p style={{ fontSize: '2em', fontWeight: 'bold' }}>Current Count: {count}</p>
  );
}

// 2. CounterButton Component
function CounterButton({ onClick, label }) {
  return (
    <button 
      onClick={onClick} 
      style={{
        margin: '5px',
        padding: '10px 20px',
        fontSize: '1em',
        cursor: 'pointer'
      }}
    >
      {label}
    </button>
  );
}

// 3. ParentCounter Component
function ParentCounter() {
  const [count, setCount] = useState(0);

  const increment = () => {
    setCount(prevCount => prevCount + 1);
  };

  const decrement = () => {
    setCount(prevCount => prevCount - 1);
  };

  return (
    <div style={{ textAlign: 'center', fontFamily: 'Arial, sans-serif' }}>
      <h2>React Counter App</h2>
      <CounterDisplay count={count} />
      <div>
        <CounterButton onClick={increment} label="Increment" />
        <CounterButton onClick={decrement} label="Decrement" />
      </div>
    </div>
  );
}

export default ParentCounter;

