enum UserStatus {
  ACTIVE = "ACTIVE",
  INACTIVE = "INACTIVE",
  PENDING = "PENDING",
}

type UserProfile = [number, string, UserStatus, boolean];

function displayUserProfile(profile: UserProfile): string {
  const [id, name, status, isAdmin] = profile;
  const adminStatus = isAdmin ? "Yes" : "No";
  return `User ID: ${id}, Name: ${name} (${status}), Admin: ${adminStatus}`;
}

// Example usage:
const user1: UserProfile = [101, "Alice Smith", UserStatus.ACTIVE, true];
const user2: UserProfile = [202, "Bob Johnson", UserStatus.INACTIVE, false];
const user3: UserProfile = [303, "Charlie Brown", UserStatus.PENDING, false];

console.log(displayUserProfile(user1));
// Expected: "User ID: 101, Name: Alice Smith (ACTIVE), Admin: Yes"

console.log(displayUserProfile(user2));
// Expected: "User ID: 202, Name: Bob Johnson (INACTIVE), Admin: No"

console.log(displayUserProfile(user3));
