import React, { useState } from 'react';

interface PasswordInputProps {
  password: string;
  setPassword: (value: string) => void;
}

const PasswordInput: React.FC<PasswordInputProps> = ({ password, setPassword }) => {
  const [isTouched, setIsTouched] = useState(false);
  const hasError = isTouched && password.length < 6;

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setPassword(e.target.value);
  };

  const handleBlur = () => {
    setIsTouched(true);
  };

  return (
    <div>
      <label htmlFor="password">Password:</label>
      <input
        type="password"
        id="password"
        name="password"
        value={password}
        onChange={handleChange}
        onBlur={handleBlur} // Detect when the input is touched/blurred
        style={{ borderColor: hasError ? 'red' : '' }}
      />
      {hasError && (
        <p style={{ color: 'red', fontSize: '0.8em' }}>Password must be at least 6 characters.</p>
      )}
    </div>
  );
};

// Parent component for demonstration
function App() {
  const [appPassword, setAppPassword] = useState('');
  const [appEmail, setAppEmail] = useState('');

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    console.log('Form Submitted:', { appEmail, appPassword });
    if (appPassword.length < 6) {
      alert('Please fix password errors before submitting.');
    }
  };

  return (
    <form onSubmit={handleSubmit} style={{ display: 'flex', flexDirection: 'column', gap: '15px', maxWidth: '300px', margin: '20px auto', padding: '20px', border: '1px solid #ccc', borderRadius: '8px' }}>
      <div>
        <label htmlFor="email">Email:</label>
        <input
