import React, { useState, useEffect } from 'react';
import { createStore, combineReducers } from 'redux';
import { Provider } from 'react-redux';
import { reduxForm, Field, reducer as formReducer } from 'redux-form';
import { connect } from 'react-redux';

// Mock Redux store setup
const initialUserState = {
  data: { firstName: '', lastName: '' }
};

function userReducer(state = initialUserState, action) {
  switch (action.type) {
    case 'SET_USER_DATA':
      return { ...state, data: action.payload };
    default:
      return state;
  }
}

const rootReducer = combineReducers({
  form: formReducer,
  user: userReducer,
});

const store = createStore(rootReducer);

// --- Your Redux-Form Component will go here ---

// Define a presentational component for the form
class UserProfileForm extends React.Component {
  render() {
    const { handleSubmit, pristine, reset, submitting } = this.props;
    return (
      <form onSubmit={handleSubmit}>
        <div>
          <label>First Name</label>
          <div>
            <Field name="firstName" component="input" type="text" />
          </div>
        </div>
        <div>
          <label>Last Name</label>
          <div>
            <Field name="lastName" component="input" type="text" />
          </div>
        </div>
        <div>
          <button type="submit" disabled={pristine || submitting}>Submit</button>
          <button type="button" disabled={pristine || submitting} onClick={reset}>Reset</button>
        </div>
        <p>Pristine: {pristine ? 'Yes' : 'No'}</p>
        <p>Submitting: {submitting ? 'Yes' : 'No'}</p>
      </form>
    );
  }
}

// Implement mapStateToProps to get initialValues from Redux state
const mapStateToProps = (state) => ({
  initialValues: state.user.data // This prop will trigger reinitialization if it changes
});

// Apply reduxForm HOC with enableReinitialize
const UserProfileFormConnected = reduxForm({
  form: 'userProfileEdit',
  enableReinitialize: true, // Crucial for dynamic initialValues
  keepDirtyOnReinitialize: false // Default behavior, but good to be aware of
})(UserProfileForm);

// Connect the form to Redux to get access to initialValues
const FinalUserProfileForm = connect(mapStateToProps)(UserProfileFormConnected);

// Main App Component to demonstrate loading users
function App() {
  const [currentUserId, setCurrentUserId] = useState(null);

  useEffect(() => {
    // Simulate initial data load for a default user
    fetchUserData('user1'); // Load a default user on mount
  }, []);

  const fetchUserData = (userId) => {
    // Simulate API call
    const userData = {
      'user1': { firstName: 'Alice', lastName: 'Smith' },
      'user2': { firstName: 'Bob', lastName: 'Johnson' },
      'user3': { firstName: 'Charlie', lastName: 'Brown' }
    };
    setTimeout(() => {
      store.dispatch({ type: 'SET_USER_DATA', payload: userData[userId] });
      setCurrentUserId(userId);
    }, 500);
  };

  const onSubmit = async (values) => {
    console.log('Form submitted:', values);
    alert(JSON.stringify(values, null, 2));
    // Simulate API submission
    return new Promise(resolve => setTimeout(resolve, 1000));
  };

  return (
    <Provider store={store}>
      <h1>Redux-Form Dynamic Reinitialization</h1>
      <button onClick={() => fetchUserData('user1')}>Load User A (Alice)</button>
      <button onClick={() => fetchUserData('user2')} style={{marginLeft: '10px'}}>Load User B (Bob)</button>
      <button onClick={() => fetchUserData('user3')} style={{marginLeft: '10px'}}>Load User C (Charlie)</button>
      <hr />
      {currentUserId ? <FinalUserProfileForm onSubmit={onSubmit} /> : <p>Loading user data...</p>}
    </Provider>
  );
}

export default App;

