import React, { useState } from 'react';

function LoginForm() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [emailError, setEmailError] = useState('');
  const [passwordError, setPasswordError] = useState('');
  const [isSubmitting, setIsSubmitting] = useState(false);

  const validateForm = () => {
    let isValid = true;
    setEmailError('');
    setPasswordError('');

    if (!email) {
      setEmailError('Email is required.');
      isValid = false;
    } else if (!/^[\w-]+(\.[\w-]+)*@([a-z0-9-]+(\.[a-z0-9-]+)*?\.[a-z]{2,6}|(\d{1,3}\.){3}\d{1,3})(:\d{4})?$/.test(email)) {
      setEmailError('Invalid email format.');
      isValid = false;
    }

    if (!password) {
      setPasswordError('Password is required.');
      isValid = false;
    } else if (password.length < 6) {
      setPasswordError('Password must be at least 6 characters.');
      isValid = false;
    }

    return isValid;
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    if (validateForm()) {
      setIsSubmitting(true);
      // Simulate API call
      try {
        await new Promise(resolve => setTimeout(resolve, 1500));
        console.log('Form submitted successfully:', { email, password });
        alert('Login successful!');
        // Clear form or redirect
      } catch (error) {
        console.error('Submission error:', error);
        alert('Login failed. Please try again.');
      } finally {
        setIsSubmitting(false);
      }
    }
  };

  return (
    <form onSubmit={handleSubmit} style={{ display: 'flex', flexDirection: 'column', gap: '10px', maxWidth: '300px', margin: '20px auto', padding: '20px', border: '1px solid #ccc', borderRadius: '5px' }}>
      <h2>Login</h2>
      <div>
        <label htmlFor="email">Email:</label>
        <input
          type="email"
          id="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          aria-required="true"
          aria-invalid={!!emailError}
          aria-describedby="email-error"
          disabled={isSubmitting}
        />
        {emailError && <div id="email-error" style={{ color: 'red', fontSize: '0.8em' }}>{emailError}</div>}
      </div>
      <div>
        <label htmlFor="password">Password:</label>
        <input
          type="password"
          id="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          aria-required="true"
          aria-invalid={!!passwordError}
          aria-describedby="password-error"
          disabled={isSubmitting}
        />
        {passwordError && <div id="password-error" style={{ color: 'red', fontSize: '0.8em' }}>{passwordError}</div>}
      </div>
      <button type="submit" disabled={isSubmitting} style={{ padding: '10px', backgroundColor: '#007bff', color: 'white', border: 'none', borderRadius: '4px', cursor: isSubmitting ? 'not-allowed' : 'pointer' }}>
        {isSubmitting ? 'Logging in...' : 'Login'}
      </button>
    </form>
  );
}

export default LoginForm;

import React from 'react';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.nameInput = null; // Initialize ref
  }

  componentDidMount() {
    // Ensure the ref is set before calling focus
    if (this.nameInput) {
      this.nameInput.focus();
    }
  }

  render() {
    return (
      <div>
        <p>Input below should automatically focus on page load.</p>
        <input
          ref={(input) => { this.nameInput = input; }} // Assign DOM node to ref property
          defaultValue="Will focus"
          aria-label="Name input"
        />
        <br/>
        <input
          defaultValue="Won't focus"
          aria-label="Another input"
        />
      </div>
    );
  }
}

export default App;

import React, { useRef, useEffect } from 'react';

function App() {
  const inputRef = useRef(null); // Create a ref object

  useEffect(() => {
    // Access the DOM node via .current property
    if (inputRef.current) {
      inputRef.current.focus();
    }
  }, []); // Empty dependency array ensures this runs only once on mount

  return (
    <div>
      <p>Input below should automatically focus on page load.</p>
      <input ref={inputRef} aria-label="Auto-focused input" />
    </div>
  );
}

export default App;

import React from 'react';

class WindowDimensions extends React.Component {
  constructor(props) {
    super(props);
    this.state = { width: 0, height: 0 };
    this.updateDimensions = this.updateDimensions.bind(this); // Bind 'this' to the handler
  }

  componentDidMount() {
    this.updateDimensions(); // Set initial dimensions
    window.addEventListener('resize', this.updateDimensions);
  }

  componentWillUnmount() {
    window.removeEventListener('resize', this.updateDimensions);
  }

  updateDimensions() {
