              <input
                type="password"
                id="password"
                name="password"
                value={formData.password}
                onChange={handleChange}
                aria-required="true"
                aria-invalid={!!errors.password}
                aria-describedby={errors.password ? 'password-error' : undefined}
                disabled={isSubmitting}
                style={{ width: '100%', padding: '8px', boxSizing: 'border-box', marginTop: '5px' }}
              />
              {errors.password && <div id="password-error" role="alert" style={{ color: 'red', fontSize: '0.8em' }}>{errors.password}</div>}
            </div>
            <div style={{ marginBottom: '15px' }}>
              <label htmlFor="confirmPassword">Confirm Password:</label>
              <input
                type="password"
                id="confirmPassword"
                name="confirmPassword"
                value={formData.confirmPassword}
                onChange={handleChange}
                aria-required="true"
                aria-invalid={!!errors.confirmPassword}
                aria-describedby={errors.confirmPassword ? 'confirm-password-error' : undefined}
                disabled={isSubmitting}
                style={{ width: '100%', padding: '8px', boxSizing: 'border-box', marginTop: '5px' }}
              />
              {errors.confirmPassword && <div id="confirm-password-error" role="alert" style={{ color: 'red', fontSize: '0.8em' }}>{errors.confirmPassword}</div>}
            </div>
            <div style={{ display: 'flex', justifyContent: 'space-between', marginTop: '20px' }}>
              <button onClick={handleBack} disabled={isSubmitting} style={{ padding: '10px 20px', backgroundColor: '#6c757d', color: 'white', border: 'none', borderRadius: '4px', cursor: isSubmitting ? 'not-allowed' : 'pointer' }}>
                Back
              </button>
              <button type="button" onClick={handleSubmit} disabled={isSubmitting} style={{ padding: '10px 20px', backgroundColor: '#007bff', color: 'white', border: 'none', borderRadius: '4px', cursor: isSubmitting ? 'not-allowed' : 'pointer' }}>
                {isSubmitting ? 'Registering...' : 'Register'}
              </button>
            </div>
          </div>
        )}
      </form>
      {errors.general && <div role="alert" aria-live="assertive" style={{ color: 'red', marginTop: '15px' }}>{errors.general}</div>}
    </div>
  );
}

export default RegistrationForm;

