import React, { useState, useEffect, useCallback } from 'react';

// --- hooks/useWindowSize.js --- START
const debounce = (func, delay) => {
  let timeoutId;
  return (...args) => {
    clearTimeout(timeoutId);
    timeoutId = setTimeout(() => {
      func.apply(this, args);
    }, delay);
  };
};

function useWindowSize(debounceDelay = 200) {
  const [dimensions, setDimensions] = useState({
    width: typeof window !== 'undefined' ? window.innerWidth : 0,
    height: typeof window !== 'undefined' ? window.innerHeight : 0,
  });

  const handleResize = useCallback(
    debounce(() => {
      setDimensions({
        width: window.innerWidth,
        height: window.innerHeight,
      });
    }, debounceDelay),
    [debounceDelay] // `handleResize` function itself will only change if `debounceDelay` changes
  );

  useEffect(() => {
    // Guard against SSR environments where `window` might not be defined
    if (typeof window === 'undefined') return;

    // Add event listener
    window.addEventListener('resize', handleResize);

    // Cleanup function: remove event listener when component unmounts
    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, [handleResize]); // Effect re-runs only if `handleResize` changes (i.e., `debounceDelay` changes)

  return dimensions;
}
// --- hooks/useWindowSize.js --- END

// --- components/LargeScreenNav.js --- START
function LargeScreenNav() {
  return (
    <nav style={{ display: 'flex', gap: '20px' }}>
      <a href="#home" style={{ color: 'white', textDecoration: 'none' }}>Home</a>
      <a href="#about" style={{ color: 'white', textDecoration: 'none' }}>About</a>
      <a href="#services" style={{ color: 'white', textDecoration: 'none' }}>Services</a>
      <a href="#contact" style={{ color: 'white', textDecoration: 'none' }}>Contact</a>
    </nav>
  );
}
// --- components/LargeScreenNav.js --- END

// --- components/HamburgerMenu.js --- START
function HamburgerMenu() {
  const [isOpen, setIsOpen] = useState(false);

  const toggleMenu = () => {
    setIsOpen(!isOpen);
  };

  return (
    <div style={{ position: 'relative' }}>
      <button onClick={toggleMenu} style={{ background: 'none', border: 'none', fontSize: '24px', cursor: 'pointer', color: 'white' }} aria-expanded={isOpen} aria-controls="mobile-menu" aria-label="Toggle navigation">

      </button>
      {isOpen && (
        <nav id="mobile-menu" style={{
          position: 'absolute', right: '0', top: '40px', background: '#444', border: '1px solid #555', padding: '10px',
          display: 'flex', flexDirection: 'column', gap: '10px', minWidth: '150px', zIndex: 100, borderRadius: '4px'
        }}>
          <a href="#home" style={{ color: 'white', textDecoration: 'none' }} onClick={() => setIsOpen(false)}>Home</a>
          <a href="#about" style={{ color: 'white', textDecoration: 'none' }} onClick={() => setIsOpen(false)}>About</a>
          <a href="#services" style={{ color: 'white', textDecoration: 'none' }} onClick={() => setIsOpen(false)}>Services</a>
          <a href="#contact" style={{ color: 'white', textDecoration: 'none' }} onClick={() => setIsOpen(false)}>Contact</a>
        </nav>
      )}
    </div>
  );
}
// --- components/HamburgerMenu.js --- END

// --- components/ResponsiveHeader.js --- START
function ResponsiveHeader() {
  const { width } = useWindowSize();
  const breakpoint = 768; // Example breakpoint in pixels

  return (
    <header style={{
      display: 'flex', justifyContent: 'space-between', alignItems: 'center',
      padding: '15px 20px', background: '#333', color: 'white'
    }}>
      <h1 style={{ margin: 0, fontSize: '1.5em' }}>MyBrand</h1>
      {width >= breakpoint ? <LargeScreenNav /> : <HamburgerMenu />}
    </header>
  );
}
// --- components/ResponsiveHeader.js --- END

// --- App.js --- START
export default function App() {
  return (
    <div style={{ fontFamily: 'Arial, sans-serif' }}>
      <ResponsiveHeader />
      <main style={{ padding: '20px' }}>
        <h2>Welcome!</h2>
        <p>Resize your browser window to see the header navigation change.</p>
        <p>Below {768}px, the full navigation will be replaced by a hamburger menu icon.</p>
        <p>Current window width (from hook): {useWindowSize().width}px</p>
        <div style={{ height: '1000px', background: '#f5f5f5', marginTop: '20px', padding: '20px' }}>
          Scrollable content to fill space
        </div>
      </main>
    </div>
  );
}
// --- App.js --- END
