  return [
    { id: 1, name: 'Laptop Pro 15', price: 1200, category: 'Electronics' },
    { id: 2, name: 'Mechanical Keyboard', price: 150, category: 'Accessories' },
    { id: 3, name: 'Wireless Mouse', price: 75, category: 'Accessories' },
