const USERS_URL = 'https://jsonplaceholder.typicode.com/users';
const PHOTOS_URL = 'https://jsonplaceholder.typicode.com/photos';

async function getUsersWithPhotos() {
  try {
    // 1. Fetch users and photos concurrently
    const [users, photos] = await Promise.all([
      fetch(USERS_URL).then(res => {
        if (!res.ok) throw new Error(`HTTP error! Users status: ${res.status}`);
        return res.json();
      }),
      fetch(PHOTOS_URL).then(res => {
        if (!res.ok) throw new Error(`HTTP error! Photos status: ${res.status}`);
        return res.json();
      })
    ]);

    // 2. Correlate photos with users
    // Create a Map for efficient lookup of photos by albumId
    const photosByAlbumId = new Map();
    photos.forEach(photo => {
      if (!photosByAlbumId.has(photo.albumId)) {
        photosByAlbumId.set(photo.albumId, []);
      }
      photosByAlbumId.get(photo.albumId).push(photo);
    });

    // Attach photos array to each user object
    const usersWithPhotos = users.map(user => ({
      ...user,
      photos: photosByAlbumId.get(user.id) || [] // If no photos, default to empty array
    }));

    return usersWithPhotos;

  } catch (error) {
    console.error("Error fetching or correlating data:", error);
    throw error; // Re-throw the error to calling context
  }
}

