import React, { useState, useEffect, useMemo } from 'react';

const allUsersData = [
  { id: 1, name: 'Alice Smith', email: 'alice@example.com', address: '123 Pine St', zipCode: '10001', purchased: ['Laptop', 'Mouse'] },
  { id: 2, name: 'Bob Johnson', email: 'bob@example.com', address: '456 Oak Ave', zipCode: '20002', purchased: ['Keyboard', 'Monitor', 'Webcam'] },
  { id: 3, name: 'Charlie Brown', email: 'charlie@example.com', address: '789 Elm Rd', zipCode: '30003', purchased: ['Headphones'] },
  { id: 4, name: 'Diana Prince', email: 'diana@example.com', address: '101 Maple Ln', zipCode: '40004', purchased: ['Tablet', 'Stylus'] },
  { id: 5, name: 'Eve Adams', email: 'eve@example.com', address: '202 Birch Dr', zipCode: '50005', purchased: ['Smartwatch'] },
  { id: 6, name: 'Frank White', email: 'frank@example.com', address: '303 Cedar Ct', zipCode: '60006', purchased: ['Printer', 'Ink'] },
  { id: 7, name: 'Grace Lee', email: 'grace@example.com', address: '404 Willow Way', zipCode: '70007', purchased: ['Router'] },
  { id: 8, name: 'Henry Green', email: 'henry@example.com', address: '505 Poplar Pl', zipCode: '80008', purchased: ['SSD', 'RAM'] },
  { id: 9, name: 'Ivy King', email: 'ivy@example.com', address: '606 Spruce Sq', zipCode: '90009', purchased: ['Microphone'] },
  { id: 10, name: 'Jack Miller', email: 'jack@example.com', address: '707 Fir Fld', zipCode: '10010', purchased: ['Speaker', 'Amplifier'] },
  { id: 11, name: 'Karen Davis', email: 'karen@example.com', address: '808 Palm Pk', zipCode: '11011', purchased: ['E-reader'] },
  { id: 12, name: 'Liam Scott', email: 'liam@example.com', address: '909 Oak Gr', zipCode: '12012', purchased: ['Gaming Console', 'Controller'] }
];

function UserSearch() {
  const [searchText, setSearchText] = useState('');
  const [filteredUsers, setFilteredUsers] = useState([]); // Store all filtered users
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  const [currentPage, setCurrentPage] = useState(1); // New state for current page
  const usersPerPage = 5; // New constant for users per page

  // Effect to filter users based on search text
  useEffect(() => {
    setIsLoading(true);
    setError(null);
    setCurrentPage(1); // Reset page to 1 on search text change

    const handler = setTimeout(() => {
      try {
        const newFilteredUsers = allUsersData.filter(user =>
          user.name.toLowerCase().includes(searchText.toLowerCase())
        );
        setFilteredUsers(newFilteredUsers); // Update the full filtered list
      } catch (err) {
        setError('Failed to filter users.');
        console.error(err);
      } finally {
        setIsLoading(false);
      }
    }, 500); // Debounce for 500ms

    return () => {
      clearTimeout(handler);
    };
  }, [searchText]);

  // Calculate users for the current page
  const indexOfLastUser = currentPage * usersPerPage;
  const indexOfFirstUser = indexOfLastUser - usersPerPage;
  const currentUsers = filteredUsers.slice(indexOfFirstUser, indexOfLastUser);

  const totalPages = Math.ceil(filteredUsers.length / usersPerPage);

  const handlePageChange = (pageNumber) => {
    setCurrentPage(pageNumber);
  };

  return (
    <div className="user-search-container">
      <h1>User Search</h1>
      <input
        type="text"
        placeholder="Search users by name..."
        value={searchText}
        onChange={(e) => setSearchText(e.target.value)}
        className="search-input"
      />

      <div className="user-list-section">
        {isLoading ? (
          <div className="loading">Loading...</div>
        ) : error ? (
          <div className="error">Error: {error}</div>
        ) : (
          <div className="user-cards-container">
            {currentUsers.length > 0 ? (
              currentUsers.map((user) => (
                <div key={user.id} className="user-card">
                  <h3>{user.name}</h3>
                  <p>Email: {user.email}</p>
                  <div className="user-details">
                    <div>Address: {user.address}</div>
                    <div>Zip: {user.zipCode}</div>
                    <div>Purchased: {user.purchased.join(', ')}</div>
                  </div>
                </div>
              ))
            ) : (
              <div className="no-results">No results found</div>
            )}
          </div>
        )}

        {/* Pagination Controls */}
        {!isLoading && !error && filteredUsers.length > 0 && (
          <div className="pagination-controls">
            <button
              onClick={() => handlePageChange(currentPage - 1)}
              disabled={currentPage === 1}
            >
              Previous
            </button>
            <span>Page {currentPage} of {totalPages}</span>
            <button
              onClick={() => handlePageChange(currentPage + 1)}
              disabled={currentPage === totalPages}
            >
              Next
            </button>
          </div>
        )}
      </div>
    </div>
  );
}

export default UserSearch;
