function findMostConsecutiveChar(str) {
  if (!str || str.length === 0) {
    return { char: '', count: 0 };
  }

  let maxChar = str[0];
  let maxCount = 1;
  let currentChar = str[0];
  let currentCount = 1;

  for (let i = 1; i < str.length; i++) {
    if (str[i] === currentChar) {
      currentCount++;
    } else {
      if (currentCount > maxCount) {
        maxCount = currentCount;
        maxChar = currentChar;
      }
      currentChar = str[i];
      currentCount = 1;
    }
  }

  // Check for the last character sequence
  if (currentCount > maxCount) {
    maxCount = currentCount;
    maxChar = currentChar;
  }

  return { char: maxChar, count: maxCount };
}

// Example Usage:
