import { fromJS } from 'immutable';

const initialState = fromJS({
  user: null,
  isLoading: false,
  error: null
});

function userReducer(state = initialState, action) {
  switch (action.type) {
    case 'SET_USER':
      // Converts action.payload to Immutable.Map before setting
      return state.set('user', fromJS(action.payload));
    case 'CLEAR_USER':
      // Sets user to null
      return state.set('user', null);
    case 'SET_LOADING':
      // Sets isLoading to a boolean payload
      return state.set('isLoading', action.payload);
    case 'SET_ERROR':
      // Sets error to a payload value
      return state.set('error', action.payload);
    default:
      return state;
  }
}

