let xPos = 0;
const img = new Image();
img.onload = function() {
  function animate() {
    // Clear canvas for next frame
    ctx.clearRect(0, 0, canvas.width, canvas.height);

    // Update position
    xPos = (xPos + 1) % canvas.width; 

    // Draw image at new position
    ctx.drawImage(img, xPos, 50, 100, 100); // img, x, y, width, height

    // Request next frame
    requestAnimationFrame(animate);
  }

  // Start animation after image loads
  animate();
};
