function findLocalMaxima(arr) {
  if (arr.length === 0) return [];
  if (arr.length === 1) return [0]; // Single element is always a local maximum

  const localMaxima = [];

  // Check first element
  if (arr[0] > arr[1]) {
    localMaxima.push(0);
  }

  // Check middle elements
  for (let i = 1; i < arr.length - 1; i++) {
    if (arr[i] > arr[i-1] && arr[i] > arr[i+1]) {
      localMaxima.push(i);
    }
  }

  // Check last element
  if (arr[arr.length-1] > arr[arr.length-2]) {
    localMaxima.push(arr.length-1);
  }

  return localMaxima;
}

// Example Usage:
const array1 = [1, 2, 3, 5, 4];
console.log(`Array: [${array1}] | Local Maxima Indices: ${findLocalMaxima(array1)}`); // Expected: [3] (value 5)

const array2 = [10, 5, 20, 15, 30];
