class Coffee {
  constructor() {
    this.price = 5;
    this.description = 'Basic Coffee';
  }

  cost() {
    return this.price;
  }

  getDescription() {
    return this.description;
  }
}

function Milk(coffee) {
  const baseCost = coffee.cost();
  const baseDescription = coffee.getDescription();

  coffee.cost = function() {
    return baseCost + 1; // Add milk cost
  };

  coffee.getDescription = function() {
    return baseDescription + ', Milk';
  };
  return coffee;
}

function Whip(coffee) {
  const baseCost = coffee.cost();
  const baseDescription = coffee.getDescription();

  coffee.cost = function() {
    return baseCost + 2; // Add whip cost
  };

  coffee.getDescription = function() {
    return baseDescription + ', Whip';
  };
  return coffee;
}

function Vanilla(coffee) {
  const baseCost = coffee.cost();
  const baseDescription = coffee.getDescription();

  coffee.cost = function() {
    return baseCost + 3; // Add vanilla cost
  };

  coffee.getDescription = function() {
    return baseDescription + ', Vanilla';
  };
  return coffee;
}

const myCoffee = new Coffee();
console.log(`Base: ${myCoffee.getDescription()} - $${myCoffee.cost()}`); // Base: Basic Coffee - $5

const milkCoffee = Milk(myCoffee);
console.log(`Milk: ${milkCoffee.getDescription()} - $${milkCoffee.cost()}`); // Milk: Basic Coffee, Milk - $6

const whippedMilkCoffee = Whip(milkCoffee);
console.log(`Whipped Milk: ${whippedMilkCoffee.getDescription()} - $${whippedMilkCoffee.cost()}`); // Whipped Milk: Basic Coffee, Milk, Whip - $8

const finalCoffee = Vanilla(whippedMilkCoffee);
console.log(`Final: ${finalCoffee.getDescription()} - $${finalCoffee.cost()}`); // Final: Basic Coffee, Milk, Whip, Vanilla - $11

function groupBy(array, key) {
  return array.reduce((result, item) => {
    // If the key doesn't exist in result, initialize it as an empty array
    (result[item[key]] = result[item[key]] || []).push(item);
    return result;
  }, {}); // Initial accumulator is an empty object
}

