function memoize(fn) {
  const cache = {};
  return function(...args) {
    // Create a unique key from arguments. JSON.stringify works for simple types.
    // For complex objects, it might not be suitable if order or non-enumerable props matter.
    const key = JSON.stringify(args);
    if (cache[key]) {
