// Parent component holds the shared state
function TemperatureCalculator() {
  const [temperature, setTemperature] = useState('');
  const [scale, setScale] = useState('c');

  // Conversion functions
  const toCelsius = (fahrenheit) => {
    if (isNaN(parseFloat(fahrenheit))) return '';
    return ((parseFloat(fahrenheit) - 32) * 5 / 9).toFixed(2);
  };
  const toFahrenheit = (celsius) => {
    if (isNaN(parseFloat(celsius))) return '';
    return ((parseFloat(celsius) * 9 / 5) + 32).toFixed(2);
  };

  // Handle temperature change from Celsius input
  const handleCelsiusChange = (value) => {
    setTemperature(value);
    setScale('c');
  };

  // Handle temperature change from Fahrenheit input
  const handleFahrenheitChange = (value) => {
    setTemperature(value);
    setScale('f');
  };

  // Calculate both temperatures based on current scale
  const celsius = scale === 'f' ? toCelsius(temperature) : temperature;
  const fahrenheit = scale === 'c' ? toFahrenheit(temperature) : temperature;

  return (
    <div>
      <h2>Temperature Converter</h2>
      <TemperatureInput 
        scale="c" 
        temperature={celsius}
        onTemperatureChange={handleCelsiusChange} 
      />
      <TemperatureInput 
        scale="f" 
        temperature={fahrenheit}
        onTemperatureChange={handleFahrenheitChange} 
      />
      <BoilingVerdict celsius={parseFloat(celsius)} />
    </div>
  );
}

