import React, { useReducer } from 'react';

// Reducer function: defines how state changes based on actions
function counterReducer(state, action) {
  switch (action.type) {
    case 'increment':
      return { count: state.count + 1 };
    case 'decrement':
      return { count: state.count - 1 };
    case 'reset':
      return { count: 0 }; // Example of another action type
    default:
      throw new Error(`Unknown action type: ${action.type}`);
  }
}

function Counter() {
  // Initialize state with useReducer: [current_state, dispatch_function]
  // The second argument is the initial state: { count: 0 }
  const [state, dispatch] = useReducer(counterReducer, { count: 0 });

  return (
    <div>
      <h2>Count: {state.count}</h2>
      <button onClick={() => dispatch({ type: 'decrement' })}>-</button>
      <button onClick={() => dispatch({ type: 'increment' })}>+</button>
      <button onClick={() => dispatch({ type: 'reset' })}>Reset</button>
    </div>
  );
}

export default Counter;

import React, { useReducer } from 'react';

// Reducer function (same as before)
function counterReducer(state, action) {
  switch (action.type) {
    case 'increment':
      return { count: state.count + 1 };
    case 'decrement':
      return { count: state.count - 1 };
    default:
      throw new Error();
  }
}

// Initializer function: computes the initial state lazily
// It receives the initialArg (passed as the second argument to useReducer)
// and returns the actual initial state.
function init(initialCount) {
  console.log('Initializing state lazily...');
  return { count: initialCount };
}

function LazyCounter({ initialCount = 0 }) {
  // Using the initializer function as the third argument
  const [state, dispatch] = useReducer(counterReducer, initialCount, init);

  return (
    <div>
      <h2>Lazy Initialized Count: {state.count}</h2>
      <button onClick={() => dispatch({ type: 'decrement' })}>-</button>
      <button onClick={() => dispatch({ type: 'increment' })}>+</button>
    </div>
  );
}

export default LazyCounter;

