import React, { useState, useEffect } from 'react';

function ProfilePage({ userId }) {
  const [user, setUser] = useState(null);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    let isMounted = true; // Flag to track component mount status
    setLoading(true); // Indicate loading state
    setError(null); // Clear previous errors

    fetch(`/api/users/${userId}`)
      .then(response => {
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        return response.json();
      })
      .then(data => {
        if (isMounted) { // Only update if component is still mounted
          setUser(data);
          setLoading(false);
        }
      })
      .catch(err => {
        if (isMounted) { // Only update if component is still mounted
          setError(err);
          setLoading(false);
        }
      });

    return () => {
      isMounted = false; // Set flag to false when component unmounts or effect re-runs
    };
  }, [userId]); // Re-run effect when userId changes

  const userNameDisplay = user ? `${user.firstName} ${user.lastName}` : 'Loading...';

  if (loading) return <div>Loading user profile...</div>;
  if (error) return <div>Error: {error.message}</div>;

  return (
    <div>
      <h2>{userNameDisplay}</h2>
      <div>Email: {user.email}</div>
    </div>
  );
}

