import React, { useState, useEffect } from 'react';

function FetchDataComponent() {
  const [data, setData] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      setIsLoading(true);
      try {
        const response = await fetch('https://api.example.com/data');
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        const result = await response.json();
        setData(result);
        setError(null);
      } catch (err) {
        setError(err.message);
        setData(null);
      } finally {
        setIsLoading(false);
      }
    };

    fetchData();
  }, []); // Empty dependency array means this effect runs once on mount

  if (isLoading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;
  if (!data) return <div>No data found</div>; // Check for null or empty data

  return (
    <div>
      <h2>Data:</h2>
      <pre>{JSON.stringify(data, null, 2)}</pre>
    </div>
  );
}

import React from 'react';

class NewsContainer extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      news: [],      // Will hold the data from the API
      isLoading: false, // Tracks if a request is in progress
      error: null   // Stores any error that occurs
    };
  }

  componentDidMount() {
    this.setState({ isLoading: true });

    fetch('https://api.example.com/news')
      .then(response => {
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        return response.json();
      })
      .then(data => {
        this.setState({
          news: data,
          isLoading: false
        });
      })
      .catch(error => {
        this.setState({
          error: error.message,
          isLoading: false
        });
      });
  }

  render() {
    const { news, isLoading, error } = this.state;

    if (isLoading) {
      return <div className="loading-spinner">Loading news...</div>;
    }

    if (error) {
      return <div className="error-message">Error loading news: {error}</div>;
    }

    if (news.length === 0) {
      return <div className="empty-message">No news available</div>;
    }

    return (
      <div className="news-container">
        <h2>Latest News</h2>
        <ul>
          {news.map(item => (
            <li key={item.id}>{item.title}</li> // Assuming 'item' has an 'id' and 'title'
          ))}
        </ul>
      </div>
    );
  }
}

import React from 'react';

class DataFetcher extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      data: null,
      isLoading: false,
      error: null
    };
  }

  componentDidMount() {
    this.fetchData();
  }

  async fetchData() {
    this.setState({ isLoading: true, error: null }); // Reset error on new fetch
    try {
      const response = await fetch('https://jsonplaceholder.typicode.com/posts/1');
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const data = await response.json();
      this.setState({ data, isLoading: false });
    } catch (error) {
      this.setState({ error: error.message, isLoading: false });
    }
  }

  render() {
    const { data, isLoading, error } = this.state;

    if (isLoading) return <p>Loading data...</p>;
    if (error) return <p>Error: {error}</p>;
    if (!data) return <p>No data available yet. Please wait.</p>;

    return (
      <div>
        <h2>Fetched Data:</h2>
        <p>Title: {data.title}</p>
        <p>Body: {data.body}</p>
      </div>
    );
  }
}

