// 1. Create a context
const UserContext = createContext(undefined); // Use undefined as default to enforce provider presence

// Mock authentication service
const authService = {
  login: async (credentials) => {
    // Simulate API call
    return new Promise(resolve => {
      setTimeout(() => {
        if (credentials.username === 'test' && credentials.password === 'password') {
          resolve({ id: 1, name: 'John Doe', email: 'john@example.com' });
        } else {
          throw new Error('Invalid credentials');
        }
      }, 500);
    });
  },
  logout: async () => {
    // Simulate API call
    return new Promise(resolve => setTimeout(() => resolve(true), 200));
  }
};

// 2. Create a provider component that manages shared state
function UserProvider({ children }) {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(false);

  const login = async (credentials) => {
    setLoading(true);
    try {
      const userData = await authService.login(credentials);
      setUser(userData);
      return { success: true };
    } catch (error) {
      return { success: false, error: error.message };
    } finally {
      setLoading(false);
    }
  };

  const logout = async () => {
    await authService.logout(); // Simulate logout API call
    setUser(null);
  };

  const value = {
    user,
    loading,
    login,
    logout
  };

  return <UserContext.Provider value={value}>{children}</UserContext.Provider>;
}

// 3. Create a custom hook for consuming the context
function useUser() {
  const context = useContext(UserContext);
  if (context === undefined) {
    throw new Error('useUser must be used within a UserProvider');
  }
  return context;
}

