import { useState, useCallback } from 'react';

/**
 * A custom hook to manage a numerical counter state.
 * @param {number} initialValue The initial value of the counter.
 * @returns {{count: number, increment: () => void, decrement: () => void, reset: () => void}}
 */
function useCounter(initialValue = 0) {
  const [count, setCount] = useState(initialValue);

  const increment = useCallback(() => {
    setCount(prevCount => prevCount + 1);
  }, []);

  const decrement = useCallback(() => {
    setCount(prevCount => prevCount - 1);
  }, []);

  const reset = useCallback(() => {
    setCount(initialValue);
  }, [initialValue]);

  return { count, increment, decrement, reset };
}

import { renderHook, act } from '@testing-library/react-hooks';
import { useCounter } from './useCounter'; // Assuming useCounter is in useCounter.js

test('should increment counter', () => {
  const { result } = renderHook(() => useCounter(0));

  act(() => {
    result.current.increment();
  });

  expect(result.current.count).toBe(1);
});

test('should decrement counter', () => {
  const { result } = renderHook(() => useCounter(10));

  act(() => {
    result.current.decrement();
  });

  expect(result.current.count).toBe(9);
});

test('should reset counter to initial value', () => {
  const { result } = renderHook(() => useCounter(5));
  act(() => {
    result.current.increment();
    result.current.increment();
  });
  expect(result.current.count).toBe(7);

  act(() => {
    result.current.reset();
  });
  expect(result.current.count).toBe(5);
});

