import React from 'react';
import PropTypes from 'prop-types';

function UserProfile(props) {
  return (
    <div>
      <h2>{props.name}</h2>
      <p>Age: {props.age}</p>
      <p>Email: {props.contactInfo.email}</p>
      <p>Hobbies: {props.hobbies.join(', ')}</p>
    </div>
  );
}

UserProfile.defaultProps = {
  age: 25,
  hobbies: [],
  contactInfo: { email: 'user@example.com' }
};

UserProfile.propTypes = {
  name: PropTypes.string.isRequired,
  age: PropTypes.number,
  hobbies: PropTypes.arrayOf(PropTypes.string),
  contactInfo: PropTypes.shape({
    email: PropTypes.string.isRequired
  })
};

// Usage example (name is required, others will use default props if not provided):
// <UserProfile name="Alice" /> 
//    -> Renders Alice, Age: 25, Email: user@example.com, Hobbies: 
// <UserProfile name="Bob" age={30} />
//    -> Renders Bob, Age: 30, Email: user@example.com, Hobbies: 
// <UserProfile name="Charlie" hobbies={['reading', 'hiking']} />
//    -> Renders Charlie, Age: 25, Email: user@example.com, Hobbies: reading, hiking

// @flow
import * as React from 'react';

type UserProfileProps = {
  name: string,
  age?: number, // Optional prop
  hobbies?: Array<string>,
  contactInfo?: { email: string }
};

function UserProfile(props: UserProfileProps): React.Node {
  const { name, age, hobbies, contactInfo } = props;
  const defaultAge = age === undefined ? 25 : age;
  const defaultHobbies = hobbies === undefined ? [] : hobbies;
  const defaultContactInfo = contactInfo === undefined ? { email: 'user@example.com' } : contactInfo;

  return (
    <div>
      <h2>{name}</h2>
      <p>Age: {defaultAge}</p>
      <p>Email: {defaultContactInfo.email}</p>
      <p>Hobbies: {defaultHobbies.join(', ')}</p>
    </div>
  );
}

export default UserProfile;

import React from 'react';
import { connect } from 'react-redux';
import { reduxForm, Field } from 'redux-form';

// A simple presentational form component
class UserEditFormComponent extends React.Component {
  render() {
    const { handleSubmit, pristine, reset, submitting } = this.props;
    return (
      <form onSubmit={handleSubmit}>
        <div>
          <label>First Name</label>
          <div>
            <Field name="firstName" component="input" type="text" placeholder="First Name" />
          </div>
        </div>
        <div>
          <label>Last Name</label>
          <div>
            <Field name="lastName" component="input" type="text" placeholder="Last Name" />
          </div>
        </div>
        <div>
          <button type="submit" disabled={pristine || submitting}>Submit</button>
          <button type="button" disabled={pristine || submitting} onClick={reset}>Clear Values</button>
        </div>
      </form>
    );
  }
}

// mapStateToProps provides initialValues from Redux state
const mapStateToProps = (state) => ({
  initialValues: state.user.data // Pre-populate form with this data
});

// Apply reduxForm HOC with enableReinitialize set to true
const UserEditFormConnected = reduxForm({
  form: 'userEdit',
  enableReinitialize: true // This is the key setting
})(UserEditFormComponent);

// Connect to Redux store to get user data for initialValues
export default connect(mapStateToProps)(UserEditFormConnected);

import React, { useRef, useEffect, useState } from 'react';

interface LazyImageProps {
  src: string;
  placeholder: string;
  alt: string;
}

function LazyImage({ src, placeholder, alt }: LazyImageProps) {
  const imgRef = useRef<HTMLImageElement>(null);
  const [isLoaded, setIsLoaded] = useState(false);

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        if (entries[0].isIntersecting) {
          setIsLoaded(true);
          observer.disconnect(); // Stop observing once loaded
        }
      },
      { rootMargin: '0px 0px 50px 0px' } // Load when 50px from bottom of viewport
    );

    if (imgRef.current) {
      observer.observe(imgRef.current);
    }

    // Clean up observer on component unmount
    return () => {
      if (imgRef.current) {
        observer.unobserve(imgRef.current);
      }
      observer.disconnect();
    };
  }, []); // Empty dependency array means this effect runs once on mount

  return (
    <img
      ref={imgRef}
      src={isLoaded ? src : placeholder}
      alt={alt}
      style={{ opacity: isLoaded ? 1 : 0.5, transition: 'opacity 0.3s ease-in-out' }} // Visual transition
    />
  );
}
